import React, { useState, useEffect } from "react";
import axios from "axios";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/allHoldings", { withCredentials: true })
      .then((res) => setAllHoldings(res.data))
      .catch((err) => console.error("Holdings fetch error:", err));
  }, []);

  return (
    <>
      <h3>Holdings ({allHoldings.length})</h3>
      <table>
        <thead>
          <tr>
            <th>Instrument</th>
            <th>Qty.</th>
            <th>Avg. cost</th>
            <th>LTP</th>
            <th>Cur. val</th>
            <th>P&L</th>
            <th>Net chg.</th>
            <th>Day chg.</th>
          </tr>
        </thead>
        <tbody>
          {allHoldings.map((stock, index) => {
            const qty = stock.qty ?? 0;
            const avg = stock.avg ?? 0;
            const price = stock.price ?? 0;
            const curValue = price * qty;
            const pl = curValue - avg * qty;
            const isProfit = pl >= 0;

            return (
              <tr key={index}>
                <td>{stock.name ?? "N/A"}</td>
                <td>{qty}</td>
                <td>{avg.toFixed(2)}</td>
                <td>{price.toFixed(2)}</td>
                <td>{curValue.toFixed(2)}</td>
                <td style={{ color: isProfit ? "green" : "red" }}>
                  {pl.toFixed(2)}
                </td>
                <td style={{ color: isProfit ? "green" : "red" }}>
                  {stock.net ?? "0"}
                </td>
                <td style={{ color: stock.isLoss ? "red" : "green" }}>
                  {stock.day ?? "0"}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};

export default Holdings;
