import React, { useState, useEffect } from "react";
import axios from "axios";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/allHoldings", { withCredentials: true })
      .then((res) => setAllHoldings(res.data))
      .catch((err) => console.error("Holdings fetch error:", err));
  }, []);

  return (
    <>
      <h3>Holdings ({allHoldings.length})</h3>
      <table>
        <tr>
          <th>Instrument</th>
          <th>Qty.</th>
          <th>Avg. cost</th>
          <th>LTP</th>
          <th>Cur. val</th>
          <th>P&L</th>
          <th>Net chg.</th>
          <th>Day chg.</th>
        </tr>
        {allHoldings.map((stock, index) => {
          const curValue = stock.price * stock.qty;
          const isProfit = curValue - stock.avg * stock.qty >= 0;
          return (
            <tr key={index}>
              <td>{stock.name}</td>
              <td>{stock.qty}</td>
              <td>{stock.avg.toFixed(2)}</td>
              <td>{stock.price.toFixed(2)}</td>
              <td>{curValue.toFixed(2)}</td>
              <td style={{ color: isProfit ? "green" : "red" }}>
                {(curValue - stock.avg * stock.qty).toFixed(2)}
              </td>
              <td style={{ color: isProfit ? "green" : "red" }}>{stock.net}</td>
              <td style={{ color: stock.isLoss ? "red" : "green" }}>
                {stock.day}
              </td>
            </tr>
          );
        })}
      </table>
    </>
  );
};

export default Holdings;
